// src/components/RelatedItems.jsx
import React, { useState } from 'react'; // ThÃªm useState vÃ o import
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import "../styles/RelatedItems.css";

// HÃ m Ã¡nh xáº¡ size tá»« giao diá»‡n sang database
const mapSizeToDatabase = (size) => {
  switch (size) {
    case '0.25': return '250';
    case '0.5': return '500';
    case '1': return '1000';
    default: return size;
  }
};

// HÃ m Ã¡nh xáº¡ size Ä‘á»ƒ hiá»ƒn thá»‹
const mapSizeToDisplay = (size) => {
  switch (size) {
    case '0.25': return '0.25kg';
    case '0.5': return '0.5kg';
    case '1': return '1kg';
    default: return `${size}kg`;
  }
};

const RelatedItems = ({ relatedProducts, currentProductId, addToCart }) => {
  const navigate = useNavigate();
  const { user } = useAuth();
  const [error, setError] = useState(null);

  console.log('Related products in RelatedItems:', relatedProducts);

  const getPriceFromSchema = (product, size) => {
    if (!product || !product.prices) {
      console.error("Dá»¯ liá»‡u prices khÃ´ng tá»“n táº¡i cho sáº£n pháº©m", product?.name);
      return undefined;
    }

    const dbSize = mapSizeToDatabase(size);
    const price = product.prices[dbSize]; // Truy cáº­p trá»±c tiáº¿p tá»« prices (Map hoáº·c Object)

    console.log(`GiÃ¡ cho trá»ng lÆ°á»£ng ${size} (key: ${dbSize}):`, price);

    if (price === undefined) {
      console.error("KhÃ´ng tÃ¬m tháº¥y giÃ¡ cho trá»ng lÆ°á»£ng", size, "cá»§a sáº£n pháº©m", product.name);
      return undefined;
    }
    return price;
  };

  const proceedToPayment = (product) => {
    const size = '0.25';
    const price = getPriceFromSchema(product, size);
    if (!price) {
      setError("GiÃ¡ sáº£n pháº©m chÆ°a Ä‘Æ°á»£c cáº­p nháº­t. Vui lÃ²ng thá»­ láº¡i sau.");
      return;
    }

    const dbSize = mapSizeToDatabase(size);

    const item = {
      productId: product._id,
      name: product.name,
      price: price,
      image: product.image,
      size: dbSize,
      quantity: 1,
      selected: true,
    };

    navigate("/payment", {
      state: {
        selectedItems: [item],
        total: price * 1,
      },
    });
    setError(null);
  };

  const handleBuyNow = (product) => {
    if (!user?.token) {
      setError("Vui lÃ²ng Ä‘Äƒng nháº­p Ä‘á»ƒ tiáº¿p tá»¥c mua hÃ ng.");
      navigate('/sign-in');
      return;
    }

    proceedToPayment(product);
  };

  return (
    <div className="related-items">
      <h3>Sáº£n pháº©m cÃ¹ng loáº¡i</h3>
      {error && <p className="error-message">{error}</p>}

      <div className="related-list">
        {relatedProducts.length > 0 ? (
          relatedProducts
            .filter(product => product._id !== currentProductId)
            .map(product => {
              const size = '0.25';
              const displaySize = mapSizeToDisplay(size);
              const currentPrice = getPriceFromSchema(product, size);

              return (
                <div key={product._id} className="related-item">
                  <div
                    onClick={() => navigate(`/${product.category}/${product._id}`)}
                    style={{ cursor: 'pointer' }}
                  >
                    <img src={product.image} alt={product.name} className="related-image" />
                    <h4 className="related-name">{product.name}</h4>
                    <p className="related-price">
                      {currentPrice
                        ? `${currentPrice.toLocaleString()} VND / ${displaySize}`
                        : `GiÃ¡ chÆ°a cáº­p nháº­t ${displaySize}`}
                    </p>
                  </div>
                  <div className="related-actions">
                    <button
                      className="btn-add"
                      onClick={(e) => {
                        e.stopPropagation();
                        if (!currentPrice) {
                          setError("GiÃ¡ sáº£n pháº©m chÆ°a Ä‘Æ°á»£c cáº­p nháº­t. Vui lÃ²ng thá»­ láº¡i sau.");
                          return;
                        }
                        const dbSize = mapSizeToDatabase(size);
                        addToCart({
                          productId: product._id,
                          name: product.name,
                          price: currentPrice,
                          image: product.image,
                          size: dbSize,
                          quantity: 1,
                        });
                        setError(null);
                      }}
                      disabled={!currentPrice}
                    >
                      ðŸ›’
                    </button>
                    <button
                      className="btn-buy-now"
                      onClick={(e) => {
                        e.stopPropagation();
                        handleBuyNow(product);
                      }}
                      disabled={!currentPrice}
                    >
                      Mua ngay
                    </button>
                  </div>
                </div>
              );
            })
        ) : (
          <p>KhÃ´ng cÃ³ sáº£n pháº©m liÃªn quan.</p>
        )}
      </div>
    </div>
  );
};

export default RelatedItems;
