import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/menu.css';
import { fetchMenuItems } from '../services/menuService';
import { useCart } from '../context/CartContext';

function Menu() {
    const [fruitItems, setFruitItems] = useState([]);
    const [seafoodItems, setSeafoodItems] = useState([]);
    const [meatItems, setMeatItems] = useState([]);
    const [nutItems, setNutItems] = useState([]);
    const { addToCart } = useCart();
    const navigate = useNavigate();

    // T·∫£i d·ªØ li·ªáu cho t·ª´ng danh m·ª•c khi component mount
    useEffect(() => {
        // Tr√°i C√¢y S·∫•y
        fetchMenuItems('fruit').then((items) => {
            setFruitItems(items);
        });
        // H·∫£i S·∫£n S·∫•y
        fetchMenuItems('seafood').then((items) => {
            setSeafoodItems(items);
        });
        // Th·ªãt S·∫•y
        fetchMenuItems('meat').then((items) => {
            setMeatItems(items);
        });
        // H·∫°t S·∫•y
        fetchMenuItems('nut').then((items) => {
            setNutItems(items);
        });
    }, []);

    const handleAddToCart = (item) => {
        const price = item.prices?.["0"]?.["25"] || item.prices?.["1"];
        if (price === undefined) {
            console.error("L·ªói: Kh√¥ng t√¨m th·∫•y gi√° s·∫£n ph·∫©m", item);
            return;
        }

        addToCart({
            productId: item._id,
            name: item.name,
            price: price,
            image: item.image,
            size: "0.25",
            quantity: 1
        });
    };

    // H√†m render danh s√°ch s·∫£n ph·∫©m cho m·ªôt danh m·ª•c
    const renderMenuList = (items, categoryTitle, categoryId) => (
        <div className="menu-list">
            <h3 className="menu-category-title">{categoryTitle}</h3>
            <div id={categoryId} className="menu-content show">
                {items.map((item) => {
                    const currentPrice = item.prices?.["0"]?.["25"] || item.prices?.["1"];
                    const originalPrice = currentPrice * 1.17;
                    const discountPercentage = 17;

                    return (
                        <div className="food-items" key={item._id}>
                            <div
                                className="food-item"
                                onClick={() => navigate(`/${item.category}/${item._id}`)}
                                style={{ cursor: "pointer" }}
                            >
                                <img src={item.image} alt={item.name} />
                                <h2>{item.name}</h2>
                            </div>
                            <div className="food-price">
                                <span className="current-price">
                                    {currentPrice
                                        ? `${currentPrice.toLocaleString()} VND`
                                        : "Gi√° ch∆∞a c·∫≠p nh·∫≠t"}
                                </span>
                                {originalPrice && (
                                    <span className="original-price">
                                        {originalPrice.toLocaleString()} VND
                                    </span>
                                )}
                                {discountPercentage && (
                                    <span className="discount">-{discountPercentage}%</span>
                                )}
                            </div>
                            <div className="food-meta">
                                <span>‚≠ê {item.rating || 5.0}</span>
                                <span>ƒê√£ b√°n {item.sold || 0}</span>
                            </div>
                            <button
                                className="add-to-cart-btn"
                                onClick={(e) => {
                                    e.stopPropagation();
                                    handleAddToCart(item);
                                }}
                            >
                                üõí
                            </button>
                        </div>
                    );
                })}
            </div>
        </div>
    );

    return (
        <section className="menu section-padding" id="dactrung">
            <div className="container">
                <div className="row">
                    <div className="section-title">
                        <h2 data-title="M√≥n ƒê·∫∑c Tr∆∞ng">Th·ª±c ƒë∆°n c·ªßa ch√∫ng t√¥i</h2>
                    </div>
                </div>

                <div className="row">
                    <div className="menu-title">
                        <h2>·∫®m th·ª±c Th√°i Lan mang m·ªôt s·ª± quy·∫øn r≈© ƒë·∫∑c bi·ªát...</h2>
                    </div>
                </div>

                {/* Hi·ªÉn th·ªã 4 danh s√°ch ri√™ng bi·ªát */}
                <div className="menu-lists">
                    {renderMenuList(fruitItems, "Tr√°i C√¢y S·∫•y", "fruit")}
                    {renderMenuList(seafoodItems, "H·∫£i S·∫£n S·∫•y", "seafood")}
                    {renderMenuList(meatItems, "Th·ªãt S·∫•y", "meat")}
                    {renderMenuList(nutItems, "H·∫°t S·∫•y", "nut")}
                </div>
            </div>
        </section>
    );
}

export default Menu;
