import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/menu.css';
import { fetchMenuItems } from '../services/menuService';
import { useCart } from '../context/CartContext';

function Menu() {
    const [activeCategory, setActiveCategory] = useState('fruit');
    const [menuItems, setMenuItems] = useState([]);
    const { addToCart } = useCart();
    const navigate = useNavigate();

    const [selectedProduct, setSelectedProduct] = useState(null);
    const [selectedWeight, setSelectedWeight] = useState("25"); // Gi·ªØ "25" nh∆∞ UI

    // H√†m √°nh x·∫° gi√° tr·ªã weight t·ª´ UI sang schema
    const mapWeightToSchema = (weight) => {
        return weight === "25" ? "0.25" : weight === "5" ? "0.5" : "1";
    };

    useEffect(() => {
        fetchMenuItems(activeCategory).then(setMenuItems);
    }, [activeCategory]);

    const handleAddToCart = (item) => {
        setSelectedProduct(item);
        setSelectedWeight("25");
    };

    const confirmAddToCart = () => {
        if (selectedProduct) {
            const schemaWeight = mapWeightToSchema(selectedWeight);
            const price = selectedProduct.prices?.[schemaWeight];
            if (price === undefined) {
                console.error("L·ªói: Kh√¥ng t√¨m th·∫•y gi√° s·∫£n ph·∫©m", selectedProduct);
                return;
            }

            addToCart({
                name: selectedProduct.name,
                price: price,
                image: selectedProduct.image,
                weight: selectedWeight, // Gi·ªØ gi√° tr·ªã UI
            });
            setSelectedProduct(null);
        }
    };

    return (
        <section className="menu section-padding" id="dactrung">
            <div className="container">
                <div className="row">
                    <div className="section-title">
                        <h2 data-title="M√≥n ƒê·∫∑c Tr∆∞ng">Th·ª±c ƒë∆°n c·ªßa ch√∫ng t√¥i</h2>
                    </div>
                </div>

                <div className="row">
                    <div className="menu-title">
                        <h2>·∫®m th·ª±c Th√°i Lan mang m·ªôt s·ª± quy·∫øn r≈© ƒë·∫∑c bi·ªát...</h2>
                        <nav className="menu-nav">
                            <ul>
                                {["fruit", "seafood", "meat", "nut"].map((category) => (
                                    <li key={category}>
                                        <button
                                            onClick={() => setActiveCategory(category)}
                                            className={`menu-button ${activeCategory === category ? 'active' : ''}`}
                                        >
                                            {category === "fruit" && "Tr√°i C√¢y S·∫•y"}
                                            {category === "seafood" && "H·∫£i S·∫£n S·∫•y"}
                                            {category === "meat" && "Th·ªãt S·∫•y"}
                                            {category === "nut" && "H·∫°t S·∫•y"}
                                        </button>
                                    </li>
                                ))}
                            </ul>
                        </nav>
                    </div>
                </div>

                <div className="row justify-content">
                    <div id={activeCategory} className="menu-content show">
                        {menuItems.map((item) => (
                            <div className="row food-items" key={item._id}>
                                <div className="row food-item" onClick={() => navigate(`/${item.category}/${item._id}`)} style={{ cursor: "pointer" }}>
                                    <img src={item.image} alt={item.name} />
                                    <h2>{item.name}</h2>
                                </div>
                                <div className="food-price">
                                    <p>
                                        {item.prices?.["0.25"]
                                            ? `${item.prices["0.25"].toLocaleString()} VND / 0.25kg`
                                            : "Gi√° ch∆∞a c·∫≠p nh·∫≠t"}
                                    </p>
                                </div>
                                <button className="add-to-cart-btn" onClick={(e) => { e.stopPropagation(); handleAddToCart(item); confirmAddToCart(); }}>
                                    üõí
                                </button>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </section>
    );
}

export default Menu;
