import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import AddressService from '../services/AddressService';
import "../styles/RelatedItem.css";

const RelatedItems = ({ relatedProducts, currentProductId, addToCart }) => {
  const navigate = useNavigate();
  const { user } = useAuth();

  const handleBuyNow = async (product) => {
    const price = product.prices?.["0"]?.["25"]; // Gi√° cho size 0.25kg
    if (!price) {
      console.error("L·ªói: Kh√¥ng t√¨m th·∫•y gi√° s·∫£n ph·∫©m", product);
      return;
    }

    const item = {
      productId: product._id,
      name: product.name,
      price: price,
      image: product.image,
      size: "0.25",
      quantity: 1,
    };

    let selectedAddress = null;
    if (user && user._id) {
      try {
        const addresses = await AddressService.getAddresses(user._id); // Gi·∫£ ƒë·ªãnh API l·∫•y ƒë·ªãa ch·ªâ
        selectedAddress = addresses.find((addr) => addr.isDefault) || addresses[0] || null;
      } catch (error) {
        console.error("L·ªói khi l·∫•y ƒë·ªãa ch·ªâ:", error);
      }
    }

    navigate("/payment", {
      state: {
        selectedItems: [item],
        total: price * 1, // T·ªïng ti·ªÅn cho 1 s·∫£n ph·∫©m
        selectedAddress: selectedAddress
          ? {
              fullName: selectedAddress.fullName,
              address: `${selectedAddress.detail}, ${selectedAddress.ward}, ${selectedAddress.district}, ${selectedAddress.province}`,
              phone: selectedAddress.phone,
            }
          : null,
      },
    });
  };

  return (
    <div className="related-items">
      <h3>S·∫£n ph·∫©m c√πng lo·∫°i</h3>
      <div className="related-list">
        {relatedProducts.length > 0 ? (
          relatedProducts
            .filter(product => product._id !== currentProductId)
            .map(product => {
              const currentPrice = product.prices?.["0"]?.["25"];

              return (
                <div key={product._id} className="related-item">
                  <div
                    onClick={() => navigate(`/${product.category}/${product._id}`)}
                    style={{ cursor: 'pointer' }}
                  >
                    <img src={product.image} alt={product.name} className="related-image" />
                    <h4 className="related-name">{product.name}</h4>
                    <p className="related-price">
                      {currentPrice
                        ? `${currentPrice.toLocaleString()} VND / 0.25kg`
                        : "Gi√° ch∆∞a c·∫≠p nh·∫≠t"}
                    </p>
                  </div>
                  <div className="related-actions">
                    <button
                      className="btn-add"
                      onClick={(e) => {
                        e.stopPropagation();
                        addToCart({
                          productId: product._id,
                          name: product.name,
                          price: currentPrice,
                          image: product.image,
                          size: "0.25",
                          quantity: 1,
                        });
                      }}
                    >
                      üõí
                    </button>
                    <button
                      className="btn-buy"
                      onClick={(e) => {
                        e.stopPropagation();
                        handleBuyNow(product);
                      }}
                    >
                      Mua ngay
                    </button>
                  </div>
                </div>
              );
            })
        ) : (
          <p>Kh√¥ng c√≥ s·∫£n ph·∫©m li√™n quan.</p>
        )}
      </div>
    </div>
  );
};

export default RelatedItems;
