import React, { useState, useEffect } from 'react';
import { useCart } from '../context/CartContext'; // Import useCart ƒë·ªÉ l·∫•y d·ªØ li·ªáu gi·ªè h√†ng
import { GoogleGenerativeAI } from '@google/generative-ai'; // Import Google AI SDK
import '../styles/ChatBot.css'; // Import file CSS ri√™ng cho ChatBot

function ChatBot() {
    const [isChatOpen, setIsChatOpen] = useState(false);
    const [chatMessages, setChatMessages] = useState([]);
    const [userInput, setUserInput] = useState('');
    const [isChatLoading, setIsChatLoading] = useState(false);

    // L·∫•y d·ªØ li·ªáu gi·ªè h√†ng t·ª´ context
    const { cartItems } = useCart();

    // Kh·ªüi t·∫°o Gemini API
    const genAI = new GoogleGenerativeAI('YOUR_GEMINI_API_KEY'); // Thay YOUR_GEMINI_API_KEY b·∫±ng API Key c·ªßa b·∫°n
    const model = genAI.getGenerativeModel({ model: 'gemini-pro' });

    // Kh·ªüi t·∫°o tin nh·∫Øn ban ƒë·∫ßu c·ªßa chatbot
    useEffect(() => {
        const productInfo = cartItems.length > 0
            ? cartItems.map(item => `${item.name}: ${item.price.toLocaleString()} VND, S·ªë l∆∞·ª£ng: ${item.quantity}`).join('\n')
            : 'Gi·ªè h√†ng c·ªßa b·∫°n ƒëang tr·ªëng.';
        setChatMessages([
            { role: 'model', text: `Xin ch√†o! T√¥i l√† chatbot h·ªó tr·ª£ gi·ªè h√†ng. ƒê√¢y l√† danh s√°ch s·∫£n ph·∫©m trong gi·ªè h√†ng c·ªßa b·∫°n:\n${productInfo}\nH√£y h·ªèi t√¥i b·∫•t k·ª≥ c√¢u h·ªèi n√†o v·ªÅ s·∫£n ph·∫©m!` }
        ]);
    }, [cartItems]);

    const toggleChat = () => {
        setIsChatOpen(!isChatOpen);
    };

    // X·ª≠ l√Ω g·ª≠i tin nh·∫Øn ƒë·∫øn Gemini API
    const handleSendMessage = async () => {
        if (!userInput.trim()) return;

        // Th√™m tin nh·∫Øn c·ªßa ng∆∞·ªùi d√πng v√†o chat
        setChatMessages(prev => [...prev, { role: 'user', text: userInput }]);
        setIsChatLoading(true);

        try {
            // T·∫°o prompt v·ªõi th√¥ng tin s·∫£n ph·∫©m
            const productInfo = cartItems.length > 0
                ? cartItems.map(item => `${item.name}: ${item.price.toLocaleString()} VND, S·ªë l∆∞·ª£ng: ${item.quantity}`).join('\n')
                : 'Gi·ªè h√†ng c·ªßa b·∫°n ƒëang tr·ªëng.';
            const prompt = `B·∫°n l√† m·ªôt chatbot h·ªó tr·ª£ gi·ªè h√†ng. D∆∞·ªõi ƒë√¢y l√† danh s√°ch s·∫£n ph·∫©m trong gi·ªè h√†ng:\n${productInfo}\nNg∆∞·ªùi d√πng h·ªèi: ${userInput}\nH√£y tr·∫£ l·ªùi c√¢u h·ªèi c·ªßa ng∆∞·ªùi d√πng m·ªôt c√°ch ch√≠nh x√°c v√† t·ª± nhi√™n.`;

            // G·ªçi Gemini API
            const result = await model.generateContent(prompt);
            const response = await result.response;
            const text = response.text();

            // Th√™m ph·∫£n h·ªìi c·ªßa chatbot v√†o chat
            setChatMessages(prev => [...prev, { role: 'model', text }]);
        } catch (error) {
            console.error('L·ªói khi g·ªçi Gemini API:', error);
            setChatMessages(prev => [...prev, { role: 'model', text: 'Xin l·ªói, t√¥i kh√¥ng th·ªÉ tr·∫£ l·ªùi ngay b√¢y gi·ªù. Vui l√≤ng th·ª≠ l·∫°i sau!' }]);
        } finally {
            setIsChatLoading(false);
            setUserInput('');
        }
    };

    return (
        <>
            <div className="chat-icon" onClick={toggleChat}>
                üí¨
            </div>
            <div className={`chat-container ${isChatOpen ? '' : 'hidden'}`} id="chatContainer">
                <div className="chat-header">
                    <h2>Chat v·ªõi AI</h2>
                    <button className="close-chat-btn" onClick={toggleChat}>
